CREATE TABLE "auth"."swaps" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "transaction_signature" text NOT NULL, "fee_payer_id" uuid, "fee_payer_public_key" text, "fee_mint_address" text, "fee_account_address" text, "fee_amount" int8, "distributor_id" uuid, "transaction_at" time, "created_at" timestamptz NOT NULL DEFAULT now(), PRIMARY KEY ("id") , FOREIGN KEY ("fee_payer_id") REFERENCES "auth"."users"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("distributor_id") REFERENCES "dropzone"."distributors"("id") ON UPDATE restrict ON DELETE restrict);COMMENT ON TABLE "auth"."swaps" IS E'indexes swaps for calculating fee referral payments';
CREATE EXTENSION IF NOT EXISTS pgcrypto;
